import React, { useState, useEffect, useCallback } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import DnDCard from './DnDCard';
import { getFavoriteMovies, updateFavorites } from '../services/favorites';
import '../Favorites.css';

const Favorites = ({ user }) => {
  const [favoriteMovies, setFavoriteMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const loadFavoriteMovies = useCallback(async () => {
    if (!user || !user.googleId) {
      setFavoriteMovies([]);
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setError(null);
      const movies = await getFavoriteMovies(user.googleId);
      setFavoriteMovies(movies);
    } catch (err) {
      console.error('Error loading favorite movies:', err);
      setError('Failed to load favorite movies');
      setFavoriteMovies([]);
    } finally {
      setLoading(false);
    }
  }, [user]);

  useEffect(() => {
    loadFavoriteMovies();
  }, [user, loadFavoriteMovies]);

  const moveCard = useCallback((dragIndex, hoverIndex) => {
    setFavoriteMovies((prevMovies) => {
      const newMovies = [...prevMovies];
      const draggedMovie = newMovies[dragIndex];
      newMovies.splice(dragIndex, 1);
      newMovies.splice(hoverIndex, 0, draggedMovie);
      return newMovies;
    });
  }, []);

  const saveNewOrder = useCallback(async (newOrder) => {
    if (!user || !user.googleId) return;

    try {
      const favoriteIds = newOrder.map(movie => movie._id);
      await updateFavorites(user.googleId, favoriteIds);
      console.log('Favorites order saved successfully');
    } catch (err) {
      console.error('Error saving favorites order:', err);
    }
  }, [user]);

  // Debounced save effect
  useEffect(() => {
    if (!loading && favoriteMovies.length > 0) {
      const timeoutId = setTimeout(() => {
        saveNewOrder(favoriteMovies);
      }, 1000); // Save after 1 second of no changes

      return () => clearTimeout(timeoutId);
    }
  }, [favoriteMovies, saveNewOrder, loading]);

  if (loading) {
    return (
      <div className="favorites-container">
        <div className="favorites-content">
          <div className="favorites-header">
            <h1>Your Favorites</h1>
          </div>
          <div className="loading-message">Loading your favorites...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="favorites-container">
        <div className="favorites-content">
          <div className="favorites-header">
            <h1>Your Favorites</h1>
          </div>
          <div className="error-message">{error}</div>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="favorites-container">
        <div className="favorites-content">
          <div className="favorites-header">
            <h1>Your Favorites</h1>
          </div>
          <div className="login-message">Please log in to view your favorites.</div>
        </div>
      </div>
    );
  }

  if (favoriteMovies.length === 0) {
    return (
      <div className="favorites-container">
        <div className="favorites-content">
          <div className="favorites-header">
            <h1>Your Favorites</h1>
          </div>
          <div className="favorites-instructions">
            <p>Drag your favorite movies to rank them in order of preference. The order will be automatically saved as you make changes.</p>
            <p>You can also click on the star icons on the Movies page to add or remove movies from your favorites list.</p>
          </div>
          <div className="favorites-list">
            <h2>Ranked Favorites ({favoriteMovies.length})</h2>
            <div className="empty-state">
              <h3>No Favorites Yet</h3>
              <p>You haven't added any movies to your favorites yet. Go to the Movies page and click the star icons to add movies to your favorites.</p>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="favorites-container">
        <div className="favorites-content">
          <div className="favorites-header">
            <h1>Your Favorites</h1>
          </div>
          <div className="favorites-instructions">
            <p>Drag your favorite movies to rank them in order of preference. The order will be automatically saved as you make changes.</p>
            <p>You can also click on the star icons on the Movies page to add or remove movies from your favorites list.</p>
          </div>
          <div className="favorites-list">
            <h2>Ranked Favorites ({favoriteMovies.length})</h2>
            {favoriteMovies.map((movie, index) => (
              <DnDCard
                key={movie._id}
                id={movie._id}
                index={index}
                movie={movie}
                moveCard={moveCard}
                rank={index + 1}
              />
            ))}
          </div>
        </div>
      </div>
    </DndProvider>
  );
};

export default Favorites;
